import React, { useState } from 'react';
import { Link } from 'react-scroll';
import logo from '../assets/img/logo.png';
import { AiOutlineMenu } from 'react-icons/ai';

export default function Header() {
  const [isMenuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!isMenuOpen);
  };

  return (
    <div className='header-container flex justify-between text-[white] duration-300 shadow-2xl rounded-2xl bg-[#121227] max-w-[1300px] mx-auto flex-row p-3'>
      <div className='logo border-[#61dafb] border-2 rounded-lg'>
        <img src={logo} className='h-[50px]' alt='Logo' />
      </div>

      <div className={`menu justify-center items-center md:flex ${isMenuOpen ? 'open' : ''}`}>
        <ul className='menu-list flex flex-col md:flex-row gap-10 text-[white] justify-center items-center'>
          <li className='menu-item hover:scale-110 m-2 ease-in-out duration-200'>
            <Link
              activeClass='active'
              to='about'
              spy={true}
              smooth={true}
              offset={-70}
              duration={500}
              onClick={toggleMenu}
            >
              <span className='text-[#61dafb] font-bold'>1. </span>About
            </Link>
          </li>
          <li className='menu-item hover:scale-110 m-2 ease-in-out duration-200'>
            <Link
              activeClass='active'
              to='experience'
              spy={true}
              smooth={true}
              offset={-70}
              duration={500}
              onClick={toggleMenu}
            >
              <span className='text-[#61dafb] font-bold'>2. </span>Experience
            </Link>
          </li>
          <li className='menu-item hover:scale-110 m-2 ease-in-out duration-200'>
            <Link
              activeClass='active'
              to='projects'
              spy={true}
              smooth={true}
              offset={-70}
              duration={500}
              onClick={toggleMenu}
            >
              <span className='text-[#61dafb] font-bold'>3. </span>Projects
            </Link>
          </li>
          <li className='menu-item hover:scale-110 m-2 ease-in-out duration-200'>
            <Link
              activeClass='active'
              to='contact'
              spy={true}
              smooth={true}
              offset={-70}
              duration={500}
              onClick={toggleMenu}
            >
              <span className='text-[#61dafb] font-bold'>4. </span>Contact
            </Link>
          </li>
          <li className='menu-item border-[#61dafb] m-2 border-2 hover:text-[black] hover:bg-[#61dafb] rounded-xl p-2 hover:scale-110 ease-in-out duration-200'>
            <a href='https://drive.google.com/file/d/1-yEFuGpNKs_I4T8jfGcyjwh7UFCYblhX/view?usp=sharing'>
              Resume
            </a>
          </li>
        </ul>
      </div>

      <div className='md:hidden'>
        <AiOutlineMenu
          className={`text-[#61dafb] text-[30px] cursor-pointer ${isMenuOpen ? 'open' : ''}`}
          onClick={toggleMenu}
        />
      </div>
    </div>
  );
}
