import React, { useState, useEffect } from "react";
import { Link } from "react-scroll";
import logo from "../assets/img/logo.png";
import {
  AiOutlineHolder,
  AiOutlineQrcode,
  AiTwotoneAlert,
} from "react-icons/ai";

export default function Header() {
  const [toggle, setToggle] = useState(false);

  const toggleMenu = () => {
    setToggle(!toggle);
  };

  const [selectedColor, setSelectedColor] = useState("#FF5733");

  const handleColorChange = (event) => {
    setSelectedColor(event.target.value);
  };
  return (
    <div
      className={`header-container sticky top-0 flex justify-between text-white duration-300 shadow-2xl rounded-br-3xl rounded-bl-3xl`}
      style={{
        backgroundColor: selectedColor,
        maxWidth: "1230px",
        margin: "0 auto",
        display: "flex",
        flexDirection: "row",
        padding: "0.75rem",
      }}
    >
      <a href="/">
        <div className="logo border-[#61dafb] border-2 rounded-lg">
          <img src={logo} className="h-[50px]" alt="Logo" />
        </div>
      </a>

      {/* color picker  */}

      <div className=" justify-center items-center flex">
        <div>
          <h2></h2>
          <input
            type="color"
            value={selectedColor}
            onChange={handleColorChange}
          />

          <p>Header</p>
        </div>
      </div>

      <div className="menu justify-center items-center flex">
        <ul className="hidden md:flex flex-row gap-3 text-white justify-center items-center">
          <li className="menu-item hover:border-b-2 border-[#61dafb] p-1 hover:scale-110 m-2 ease-in-out duration-200">
            <Link
              activeClass="active"
              to="about"
              spy={true}
              smooth={true}
              offset={-70}
              duration={500}
            >
              <span className="text-[#61dafb] font-bold">1. </span>About
            </Link>
          </li>
          <li className="menu-item hover:border-b-2 border-[#61dafb] p-1 hover:scale-110 m-2 ease-in-out duration-200">
            <Link
              activeClass="active"
              to="experience"
              spy={true}
              smooth={true}
              offset={-70}
              duration={500}
            >
              <span className="text-[#61dafb] font-bold">2. </span>Experience
            </Link>
          </li>
          <li className="menu-item hover:border-b-2 border-[#61dafb] p-1 hover:scale-110 m-2 ease-in-out duration-200">
            <Link
              activeClass="active"
              to="projects"
              spy={true}
              smooth={true}
              offset={-70}
              duration={500}
            >
              <span className="text-[#61dafb] font-bold">3. </span>Projects
            </Link>
          </li>
          <li className="menu-item hover:border-b-2 border-[#61dafb] p-1 hover:scale-110 m-2 ease-in-out duration-200">
            <Link
              activeClass="active"
              to="contact"
              spy={true}
              smooth={true}
              offset={-70}
              duration={500}
            >
              <span className="text-[#61dafb] font-bold">4. </span>Contact
            </Link>
          </li>
          <li className="menu-item hover:border-b-2 border-[#61dafb] p-1 border-[#61dafb] m-2 border-2 hover:text-black hover:bg-[#61dafb] rounded-xl p-2 hover:scale-110 ease-in-out duration-200">
            <a href="https://drive.google.com/file/d/1-yEFuGpNKs_I4T8jfGcyjwh7UFCYblhX/view?usp=sharing">
              Resume
            </a>
          </li>
        </ul>

        <div
          className={`mobile z-999 ${
            toggle
              ? "right-0"
              : "right-[-100%] transition-transform duration-500"
          }`}
        >
          <ul
            className={`fixed md:hidden scale-105 top-20 z-999 bg-[#61dafb] w-[100%] text-black font-bold ${
              toggle
                ? "right-[0] transition-right duration-500"
                : "right-[-105%] transition-right duration-500"
            } h-[100%] gap-3 text-[white] justify-center items-center`}
          >
            <li className="menu-item hover:border-b-2 p-5 text-[22px] border-[#61dafb] p-1 hover:scale-110 m-2 ease-in-out duration-200">
              <Link
                activeClass="active"
                to="about"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                <span className="text-[#61dafb] font-bold">1. </span>About
              </Link>
            </li>
            <li className="menu-item hover:border-b-2 p-5 text-[20px] border-[#61dafb] p-1 hover:scale-110 m-2 ease-in-out duration-200">
              <Link
                activeClass="active"
                to="experience"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                <span className="text-[#61dafb] font-bold">2. </span>Experience
              </Link>
            </li>
            <li className="menu-item hover:border-b-2 p-5 text-[20px] border-[#61dafb] p-1 hover:scale-110 m-2 ease-in-out duration-200">
              <Link
                activeClass="active"
                to="projects"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                <span className="text-[#61dafb] font-bold">3. </span>Projects
              </Link>
            </li>
            <li className="menu-item hover:border-b-2 p-5 text-[20px] border-[#61dafb] p-1 hover:scale-110 m-2 ease-in-out duration-200">
              <Link
                activeClass="active"
                to="contact"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                <span className="text-[#61dafb] font-bold">4. </span>Contact
              </Link>
            </li>
            <li className="menu-item hover:border-b-2 p-5 text-[20px] border-[#61dafb] p-1 hover:scale-110 m-2 ease-in-out duration-200">
              <a
                className=" "
                href="https://drive.google.com/file/d/1-yEFuGpNKs_I4T8jfGcyjwh7UFCYblhX/view?usp=sharing"
              >
                Resume
              </a>
            </li>
          </ul>
        </div>

        <div className="md:hidden">
          {toggle ? (
            <AiOutlineHolder
              onClick={toggleMenu}
              className="text-[#61dafb] text-3xl cursor-pointer block"
            />
          ) : (
            <AiOutlineQrcode
              onClick={toggleMenu}
              className="text-[#61dafb] text-3xl cursor-pointer block"
            />
          )}
        </div>
      </div>
    </div>
  );
}
