import React, { useState, useEffect } from "react";
import { Link } from "react-scroll";
import logo from "../assets/img/logo.png";
import {
  AiOutlineHolder,
  AiOutlineQrcode,
  AiTwotoneAlert,
} from "react-icons/ai";

export default function Header() {
  const [toggle, setToggle] = useState(false);

  const toggleMenu = () => {
    setToggle(!toggle);
  };

  return (
    <div
      className={`textcolor header-container sticky top-0 flex justify-between  duration-300 shadow-2xl rounded-br-3xl rounded-bl-3xl`}
      style={{
        backgroundColor: selectedColor,
        maxWidth: "1230px",
        margin: "0 auto",
        display: "flex",
        flexDirection: "row",
        padding: "0.75rem",
      }}
    >
      <a href="/">
        <div className="logo border-[#61dafb] border-2 rounded-lg">
          <img src={logo} className="h-[50px]" alt="Logo" />
        </div>
      </a>
    </div>
  );
}
